queries.query:
{
  allMarkdownRemark(filter: { id: { regex: "//posts|pages//" } }) {
    edges {
      node {
        objectID: id
        fields {
          slug
        }
        internal {
          content
        }
        frontmatter {
          title
          subTitle
        }
      }
    }
  }
}

pluginOptions.query:
{
  site {
    siteMetadata {
      title
      description
      siteUrl
      site_url: siteUrl
    }
  }
}

feeds.query:
{
  allMarkdownRemark(
    limit: 1000,
    sort: { order: DESC, fields: [fields___prefix] },
    filter: { id: { regex: "//posts//" } }
  ) {
    edges {
      node {
        excerpt
        html
        fields {
          slug
          prefix
        }
        frontmatter {
          title
        }
      }
    }
  }
}
